import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // ðŸ”’ Redirect to login if token doesn't exist
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
  }, [navigate]);

  // ðŸ‘¤ Handle profile fetch
  const handleProfileClick = async () => {
    const token = localStorage.getItem('token');
    const email = localStorage.getItem('email');

    if (!token || !email) {
      alert('You are not logged in');
      return;
    }

    try {
      const response = await axios.get(`http://localhost:5144/Admin/Profile?email=${email}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setUser(response.data);
      setShowProfile(true);
    } catch (error) {
      console.error('Error fetching admin profile:', error);
      alert('Failed to load profile. Please try again.');
    }
  };

  // ðŸšª Logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('email');
    navigate('/login'); // Ensure redirection after logout
  };

  return (
    <div className="container mt-5">
      <h1>Welcome to the Dashboard</h1>

      <div className="d-flex justify-content-end mb-3">
        <button className="btn btn-primary me-2" onClick={handleProfileClick}>
          My Profile
        </button>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {showProfile && user && (
        <div className="card p-3 shadow">
          <h3>My Profile</h3>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Role:</strong> {user.role}</p>
          <p><strong>Permissions:</strong> {user.permissions}</p>
        </div>
      )}

      <Categories />
    </div>
  );
}

export default Welcome;
