import React, { useState } from 'react';
import { registerUser } from '../services/UserService';
import { Link, useNavigate } from 'react-router-dom';

function Register() {
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    contactNumber: '',
    role: '',
    shippingAddress: '',
    paymentDetails: ''
  });

  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await registerUser(form);
      alert('Registered successfully!');
      navigate('/login');
    } catch (err) {
      console.error('Registration error:', err.response?.data || err.message);
      alert('Registration failed: ' + (err.response?.data?.message || err.message));
    }
  };

  // Adjusted styles for smaller form
  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    background: 'linear-gradient(to right, #43cea2, #185a9d)',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  };

  const boxStyle = {
    backgroundColor: 'white',
    padding: '25px 20px',
    borderRadius: '10px',
    boxShadow: '0 8px 20px rgba(0, 0, 0, 0.2)',
    width: '100%',
    maxWidth: '350px', // Smaller width
    textAlign: 'center',
  };

  const inputStyle = {
    display: 'block',
    width: '100%',
    padding: '8px 10px', // Smaller padding
    margin: '8px 0',     // Less vertical spacing
    border: '1px solid #ccc',
    borderRadius: '6px',
    fontSize: '14px',
  };

  const buttonStyle = {
    width: '100%',
    padding: '10px',
    backgroundColor: '#185a9d',
    border: 'none',
    borderRadius: '6px',
    color: 'white',
    fontSize: '15px',
    marginTop: '12px',
    cursor: 'pointer',
  };

  const linkStyle = {
    color: '#185a9d',
    textDecoration: 'none',
  };

  return (
    <div style={containerStyle}>
      <div style={boxStyle}>
        <h2 style={{ marginBottom: '15px' }}>Register</h2>
        <form onSubmit={handleRegister}>
          <input
            name="name"
            value={form.name}
            onChange={handleChange}
            placeholder="Name"
            required
            style={inputStyle}
          />
          <input
            name="email"
            value={form.email}
            onChange={handleChange}
            placeholder="Email"
            required
            style={inputStyle}
          />
          <input
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            placeholder="Password"
            required
            style={inputStyle}
          />
          <input
            name="contactNumber"
            value={form.contactNumber}
            onChange={handleChange}
            placeholder="Contact Number"
            style={inputStyle}
          />
          <input
            name="role"
            value={form.role}
            onChange={handleChange}
            placeholder="Role"
            style={inputStyle}
          />
          <input
            name="shippingAddress"
            value={form.shippingAddress}
            onChange={handleChange}
            placeholder="Shipping Address"
            required
            style={inputStyle}
          />
          <input
            name="paymentDetails"
            value={form.paymentDetails}
            onChange={handleChange}
            placeholder="Payment Details"
            required
            style={inputStyle}
          />
          <button type="submit" style={buttonStyle}>Register</button>
        </form>
        <p style={{ marginTop: '15px', fontSize: '13px' }}>
          Already have an account? <Link to="/login" style={linkStyle}>Login here</Link>
        </p>
      </div>
    </div>
  );
}

export default Register;
