import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);

  const token = localStorage.getItem('token');
  const userRole = localStorage.getItem('userRole');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userRole');
    window.location.href = '/login';
  };

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get('https://localhost:7017/User/Profile', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUser(response.data);
    } catch (error) {
      console.error('Failed to fetch user profile:', error);
    }
  };

  useEffect(() => {
    if (!user && token) {
      fetchUserProfile();
    }
  }, [token, user]);

  const handleProfileClick = () => {
    setShowProfile(true);
    fetchUserProfile(); // ensure latest profile is loaded when clicked
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Welcome, {user?.name || 'User'}!</h2>
        <div>
          <button className="btn btn-outline-primary me-2" onClick={handleProfileClick}>
            My Profile
          </button>
          <button className="btn btn-danger" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      {showProfile && user && (
        <div className="card p-4 mb-4 shadow">
          <h4 className="mb-3">User Profile</h4>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phoneNumber}</p>
          <p><strong>Address:</strong> {user.address}</p>
        </div>
      )}

      {showProfile && !user && (
        <div className="text-center my-4">Loading profile...</div>
      )}

      {!showProfile && (
        <>
          <h4 className="mb-3">Categories</h4>
          <Categories />
        </>
      )}
    </div>
  );
}

export default Welcome;
