import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';

function CategoryService() {
  const [categories, setCategories] = useState([]);
  const [newCategory, setNewCategory] = useState({ name: '' });
  const [editCategoryId, setEditCategoryId] = useState(null);
  const [editCategoryData, setEditCategoryData] = useState({});
  const [message, setMessage] = useState('');

  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  // Fetch all categories
  const fetchCategories = async () => {
    try {
      const response = await axios.get('http://localhost:5144/Category', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories:", error);
      alert("Failed to fetch category data");
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  // Add a new category
  const handleAddCategory = async () => {
    if (!newCategory.name) {
      alert("Please fill in the category name.");
      return;
    }

    try {
      const payload = {
        name: newCategory.name,
      };

      await axios.post('http://localhost:5144/Category/AddingNewCategory', payload, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessage("✅ Category added successfully");
      setNewCategory({ name: '' });
      fetchCategories();
    } catch (error) {
      alert("Failed to add category. Check permissions.");
    }
  };

  // Start editing a category
  const handleEdit = (category) => {
    setEditCategoryId(category.categoryId);
    setEditCategoryData({ ...category });
  };

  // Save edited category
  const handleUpdate = async () => {
    try {
      await axios.put('http://localhost:5144/Category/UpdateCategory', null, {
        params: {
          id: editCategoryId,
          name: editCategoryData.name
        },
        headers: { Authorization: `Bearer ${token}` }
      });

      setEditCategoryId(null);
      setEditCategoryData({});
      fetchCategories();
      setMessage("✅ Category updated successfully");
    } catch (error) {
      alert("Update failed.");
    }
  };

  // Delete a category
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this category?")) return;

    try {
      await axios.delete(`http://localhost:5181/Category/DeleteCategory?id=${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      fetchCategories();
      setMessage("✅ Category deleted successfully");
    } catch (error) {
      alert("Delete failed.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">E-commerce Category Management</h2>

      {/* Add Category */}
      <div className="mb-4">
        <h5>Add New Category</h5>
        <div className="row g-2">
          <div className="col-md-4">
            <input
              type="text"
              className="form-control"
              placeholder="Category Name (e.g., Women)"
              value={newCategory.name}
              onChange={(e) => setNewCategory({ ...newCategory, name: e.target.value })}
            />
          </div>
          <div className="col-md-2">
            <button className="btn btn-success w-100" onClick={handleAddCategory}>Add</button>
          </div>
        </div>
      </div>

      {/* Categories Table */}
      <h5>Existing Categories</h5>
      <div className="table-responsive">
        <table className="table table-bordered table-striped">
          <thead className="table-dark">
            <tr>
              <th>Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {categories.length === 0 ? (
              <tr>
                <td colSpan="2" className="text-center">No categories available.</td>
              </tr>
            ) : (
              categories.map(category => (
                <tr key={category.categoryId}>
                  <td>
                    {editCategoryId === category.categoryId ? (
                      <input
                        className="form-control"
                        value={editCategoryData.name}
                        onChange={(e) => setEditCategoryData({ ...editCategoryData, name: e.target.value })}
                      />
                    ) : (
                      category.name
                    )}
                  </td>
                  <td>
                    {editCategoryId === category.categoryId ? (
                      <button className="btn btn-warning btn-sm me-2" onClick={handleUpdate}>Save</button>
                    ) : (
                      <>
                        <button className="btn btn-primary btn-sm me-2" onClick={() => handleEdit(category)}>Edit</button>
                        <button className="btn btn-danger btn-sm" onClick={() => handleDelete(category.categoryId)}>Delete</button>
                      </>
                    )}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {message && <div className="alert alert-success mt-3">{message}</div>}
    </div>
  );
}

export default CategoryService;
