import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);

  const handleProfileClick = async () => {
    const token = localStorage.getItem("token");
    const email = localStorage.getItem("email");
    const role = localStorage.getItem("role");

    try {
      let url = "";

      if (role === "Admin") {
        url = `http://localhost:5144/Admin/Profile?email=${email}`;
      } else {
        url = `http://localhost:5144/User/Profile?email=${email}`;
      }

      const response = await axios.get(url, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      setUser(response.data);
      setShowProfile(true);
    } catch (error) {
      console.error("Error fetching profile:", error);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between">
        <button className="btn btn-primary" onClick={handleProfileClick}>
          My Profile
        </button>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {showProfile && user && (
        <div className="card mt-4 p-3">
          <h4>My Profile</h4>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone || "Not Provided"}</p>
          <p><strong>Role:</strong> {user.role}</p>
          <p><strong>Address:</strong> {user.shippingAddress}</p>
          <p><strong>Payment:</strong> {user.paymentDetails}</p>
        </div>
      )}

      <Categories />
    </div>
  );
}

export default Welcome;
