import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductService from '../services/ProductService';

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    ProductService.getProducts()
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error('Error fetching products', error);
      });
  };

  const fetchProductsByCategory = (category) => {
    ProductService.getProductsByCategory(category)
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error('Error fetching category products', error);
      });
  };

  const handleDelete = (id) => {
    ProductService.deleteProduct(id)
      .then(() => {
        fetchProducts();
      })
      .catch((error) => {
        console.error('Error deleting product', error);
      });
  };

  return (
    <div>
      <h2>Product List</h2>

      <Link to="/add" style={{ marginTop: '10px', display: 'inline-block' }}>
        Add Product
      </Link>

      <div style={{ marginTop: '20px' }}>
        <button onClick={() => fetchProductsByCategory('Woman')}>Woman</button>

        <button onClick={() => fetchProductsByCategory('Men')} style={{ marginLeft: '10px' }}>Men</button>
        <button onClick={fetchProducts} style={{ marginLeft: '10px' }}>All</button>
      </div>

      <ul style={{ marginTop: '20px' }}>
        {products.map((product) => (
          <li key={product.productId}>

            {product.productName} - ${product.price}

            <img src='~/images/woman.pjp' height={50} width={100}></img>
            <Link to={`/edit/${product.productId}`} style={{ marginLeft: '10px', backgroundColor: 'blue', color: 'white' }}>
              Edit
            </Link>
            <button onClick={() => handleDelete(product.productId)} style={{ marginLeft: '10px', backgroundColor: 'red', color: 'white' }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
