import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';

function Welcome() {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '' });

  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const userResponse = await axios.get('https://localhost:7029/User/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (userResponse.data && userResponse.data.name) {
          setUser(userResponse.data);
          setFormData({ name: userResponse.data.name, email: userResponse.data.email });
        }
      } catch (errorUser) {
        try {
          const adminResponse = await axios.get('https://localhost:7029/Admin/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });

          if (adminResponse.data && adminResponse.data.name) {
            setUser(adminResponse.data);
            setFormData({ name: adminResponse.data.name, email: adminResponse.data.email });
          }
        } catch (errorAdmin) {
          console.error('Failed to fetch profile:', errorAdmin);
        }
      }
    };

    fetchUser();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  const handleEdit = () => setEditMode(true);

  const handleSave = async () => {
    const token = localStorage.getItem('token');
    try {
      await axios.put('https://localhost:7029/User/update', formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(formData);
      setEditMode(false);
    } catch (errorUser) {
      try {
        await axios.put('https://localhost:5144/Admin/update', formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(formData);
        setEditMode(false);
      } catch (errorAdmin) {
        console.error('Failed to update profile:', errorAdmin);
      }
    }
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-end mb-3">
        <button className="btn btn-primary me-2" onClick={() => setShowProfile(false)}>Home</button>
        <button className="btn btn-secondary me-2" onClick={() => setShowProfile(true)}>My Profile</button>
        <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
      </div>

      {showProfile ? (
        <div className="card p-4">
          <h3 className="mb-3">My Profile</h3>
          {user ? (
            <div>
              {editMode ? (
                <>
                  <div className="mb-3">
                    <label className="form-label">Name</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Email</label>
                    <input
                      type="email"
                      className="form-control"
                      value={formData.email}
                      onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    />
                  </div>
                  <button className="btn btn-success" onClick={handleSave}>Save</button>
                </>
              ) : (
                <>
                  <p><strong>Name:</strong> {user.name}</p>
                  <p><strong>Email:</strong> {user.email}</p>
                  <button className="btn btn-warning" onClick={handleEdit}>Edit</button>
                </>
              )}
            </div>
          ) : (
            <p>Loading profile...</p>
          )}
        </div>
      ) : (
        <Categories />
      )}
    </div>
  );
}

export default Welcome;
