import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// ProductPanel Component
function ProductPanel({ categoryName, onAddToCart, onClose }) {
  const [products, setProducts] = useState([]);

  const productImage = {
    Jeans: "./images/Jeans.jpg",
    Tops: "./images/Tops.jpg",
    Shirt: "./images/Shirts.jpg",
    Sunscreen: "./images/Sunscreen.jpg",
    // add your images here
  };

  useEffect(() => {
    if (!categoryName) return;
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(
          `http://localhost:5144/Product/ByCategory/${categoryName}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setProducts(response.data);
      } catch (error) {
        console.error("Failed to fetch products:", error);
        setProducts([]);
      }
    };
    fetchProducts();
  }, [categoryName]);

  return (
    <div className="product-panel p-3 border rounded shadow-sm">
      <button className="btn btn-danger mb-3" onClick={onClose}>
        Close Product Panel
      </button>
      <h4>Products in "{categoryName}"</h4>
      <div className="row">
        {products.length ? (
          products.map((product) => (
            <div key={product.productId} className="col-md-3 mb-3">
              <div className="card h-100">
                <img
                  src={productImage[product.productName] || "./images/Sunscreen.jpg"}
                  alt={product.productName}
                  className="card-img-top"
                  style={{ height: "200px", objectFit: "cover" }}
                />
                <div className="card-body d-flex flex-column">
                  <h6 className="card-title">{product.productName}</h6>
                  <p className="card-text">{product.description}</p>
                  <p className="card-text text-success">Rs. {product.price}/-</p>
                  <button
                    className="btn btn-sm btn-primary mt-auto"
                    onClick={() => onAddToCart(product)}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No products found in this category.</p>
        )}
      </div>
    </div>
  );
}

// CategoryPanel Component
function CategoryPanel({ categories, onSelectCategory, onClose }) {
  const categoryImages = {
    Jeans: "./images/Jeans.jpg",
    Tops: "./images/Tops.jpg",
    Shirt: "./images/Shirts.jpg",
    Sunscreen: "./images/Sunscreen.jpg",
    // add your images here
  };

  return (
    <div className="category-panel p-3 border rounded shadow-sm">
      <button className="btn btn-danger mb-3" onClick={onClose}>
        Close Category Panel
      </button>
      <h4>Categories</h4>
      <div className="row">
        {categories.length ? (
          categories.map((category) => (
            <div
              key={category.categoryId}
              className="col-md-3 mb-3"
              style={{ cursor: "pointer" }}
              onClick={() => onSelectCategory(category.name)}
            >
              <div className="card h-100">
                <img
                  src={categoryImages[category.name] || "./images/Sunscreen.jpg"}
                  alt={category.name}
                  className="card-img-top"
                  style={{ height: "200px", objectFit: "cover" }}
                />
                <div className="card-body d-flex align-items-center justify-content-center">
                  <h6 className="card-title text-center">{category.name}</h6>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No categories found.</p>
        )}
      </div>
    </div>
  );
}

function Welcome({ logout }) {
  const [user, setUser] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showProfile, setShowProfile] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [cartLoaded, setCartLoaded] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({});

  const [categories, setCategories] = useState([]);
  const [showCategoryPanel, setShowCategoryPanel] = useState(false);
  const [showProductPanel, setShowProductPanel] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user profile and cart as usual (your existing code)
    // ...omitted here for brevity, use your existing code...

    // Fetch categories for category panel
    const fetchCategories = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get("http://localhost:5144/Category", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCategories(response.data);
      } catch (error) {
        console.error("Failed to fetch categories:", error);
      }
    };
    fetchCategories();
  }, [navigate, logout]);

  useEffect(() => {
    if (!cartLoaded) return;
    localStorage.setItem("tempCart", JSON.stringify(cartItems));
    if (user?.userId || user?.adminId) {
      localStorage.setItem(
        `cartItems_${user.userId || user.adminId}`,
        JSON.stringify(cartItems)
      );
    }
  }, [cartItems, user, cartLoaded]);

  const handleAddToCart = (product) => {
    setCartItems((prev) => [...prev, product]);
    alert("Product added to cart!");
  };

  const openCategoryPanel = () => {
    setShowCategoryPanel(true);
    setShowProductPanel(false);
  };

  const openProductPanel = (categoryName) => {
    setSelectedCategory(categoryName);
    setShowProductPanel(true);
    setShowCategoryPanel(false);
  };

  const closeProductPanel = () => {
    setShowProductPanel(false);
    setSelectedCategory(null);
    setShowCategoryPanel(true);
  };

  const closeCategoryPanel = () => {
    setShowCategoryPanel(false);
  };

  return (
    <div className="container-fluid mt-3">
      <div className="border border-2 p-4 mb-4 d-flex justify-content-between">
        <h2>Welcome to the Dashboard</h2>
        <div>
          {user?.role === "Admin" && (
            <>
              <button
                className="btn btn-outline-dark me-2"
                onClick={() => navigate("/adminpanel")}
              >
                Admin Panel
              </button>
              <button
                className="btn btn-outline-info me-2"
                onClick={() => navigate("/productlist")}
              >
                Product List
              </button>
              <button
                className="btn btn-outline-primary me-2"
                onClick={openCategoryPanel}
              >
                Category Panel
              </button>
            </>
          )}
          <button
            className="btn btn-outline-primary me-2"
            onClick={() => setShowProfile(true)}
          >
            My Profile
          </button>
          <button
            className="btn btn-outline-success me-2"
            onClick={() => setShowCart(true)}
          >
            Cart ({cartItems.length})
          </button>
          <button className="btn btn-outline-danger" onClick={logout}>
            Logout
          </button>
        </div>
      </div>

      {/* Your existing Profile, Cart, and other sections here */}

      {showCategoryPanel && (
        <CategoryPanel
          categories={categories}
          onSelectCategory={openProductPanel}
          onClose={closeCategoryPanel}
        />
      )}

      {showProductPanel && selectedCategory && (
        <ProductPanel
          categoryName={selectedCategory}
          onAddToCart={handleAddToCart}
          onClose={closeProductPanel}
        />
      )}
    </div>
  );
}

export default Welcome;
