import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Categories from '../components/cards/Categories';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Welcome({ logout }) {
  const [showProfile, setShowProfile] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Redirect if token doesn't exist
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
  }, [navigate]);

  // Fetch profile (Admin or User)
  const handleProfileClick = async () => {
    const token = localStorage.getItem('token');
    const email = localStorage.getItem('email');

    if (!token || !email) {
      alert('You are not logged in');
      return;
    }

    try {
      const tokenData = JSON.parse(atob(token.split('.')[1]));
      const role = tokenData.role || 'User';

      const endpoint =
        role === 'Admin'
          ? `http://localhost:5144/Admin/Profile?email=${email}`
          : `http://localhost:5144/User/Profile?email=${email}`;

      const response = await axios.get(endpoint, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setUser(response.data);
      setShowProfile(true);
    } catch (error) {
      console.error('Error fetching profile:', error);
      alert('Failed to load profile. Please try again.');
    }
  };

  // Update user profile
  const handleUpdate = async () => {
    if (!user) return;

    const token = localStorage.getItem('token');
    try {
      await axios.put(
        `http://localhost:5144/User/Update?id=${user.userId}`,
        user,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile.');
    }
  };

  // Delete user account
  const handleDelete = async () => {
    if (!user) return;

    const confirmDelete = window.confirm('Are you sure you want to delete your account?');
    if (!confirmDelete) return;

    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:5144/User/Delete?id=${user.userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert('Account deleted successfully.');
      logout(); // Clear app state and redirect
    } catch (error) {
      console.error('Error deleting account:', error);
      alert('Failed to delete account.');
    }
  };

  const handleLogout = () => {
    logout();
  };

  return (
    <div className="container mt-5">
      <h1>Welcome to the Dashboard</h1>

      <div className="d-flex justify-content-end mb-3">
        <button className="btn btn-primary me-2" onClick={handleProfileClick}>
          My Profile
        </button>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Profile Section */}
      {showProfile && user && (
        <div className="card p-3 shadow">
          <h3>My Profile</h3>

          <div className="mb-2">
            <label><strong>Name:</strong></label>
            <input
              type="text"
              className="form-control"
              value={user.name}
              onChange={(e) => setUser({ ...user, name: e.target.value })}
            />
          </div>

          <div className="mb-2">
            <label><strong>Email:</strong></label>
            <input
              type="email"
              className="form-control"
              value={user.email}
              onChange={(e) => setUser({ ...user, email: e.target.value })}
            />
          </div>

          {user.shippingAddress !== undefined && (
            <div className="mb-2">
              <label><strong>Shipping Address:</strong></label>
              <input
                type="text"
                className="form-control"
                value={user.shippingAddress}
                onChange={(e) =>
                  setUser({ ...user, shippingAddress: e.target.value })
                }
              />
            </div>
          )}

          {user.paymentDetails !== undefined && (
            <div className="mb-2">
              <label><strong>Payment Details:</strong></label>
              <input
                type="text"
                className="form-control"
                value={user.paymentDetails}
                onChange={(e) =>
                  setUser({ ...user, paymentDetails: e.target.value })
                }
              />
            </div>
          )}

          <div className="mb-2">
            <label><strong>Role:</strong></label>
            <input
              type="text"
              className="form-control"
              value={user.role}
              readOnly
            />
          </div>

          {user.permissions && (
            <div className="mb-2">
              <label><strong>Permissions:</strong></label>
              <input
                type="text"
                className="form-control"
                value={user.permissions}
                readOnly
              />
            </div>
          )}

          <div className="mt-3">
            <button className="btn btn-warning me-2" onClick={handleUpdate}>
              Update Profile
            </button>
            <button className="btn btn-outline-danger" onClick={handleDelete}>
              Delete Account
            </button>
          </div>
        </div>
      )}

      <Categories />
    </div>
  );
}

export default Welcome;
